import 'automapper-ts/dist/automapper';
import { Document, Model, Types } from 'mongoose'; 
import { Typegoose, ModelType, InstanceType } from 'typegoose';

export abstract class BaseService<T extends Typegoose> {

    protected _model: ModelType<T>;
    protected _mapper: AutoMapperJs.AutoMapper;

    private get modelName(): string {
        return this._model.modelName;
    }

    private get viewModelName(): string {
        return `${this._model.modelName}Vm`;
    }

    async map<K>(object: Partial<InstanceType<T>> | Partial<InstanceType<T>>[], isArray: boolean = false, sourceKey ?: string, destinationKey ?: string): Promise<K> {
        const _sourceKey = isArray ? `${sourceKey || this.modelName}[]` : sourceKey || this.modelName;
        const _destinaionKey = isArray ? `${destinationKey || this.viewModelName}[]` : destinationKey || this.viewModelName;

        return this._mapper.map(_sourceKey, _destinaionKey, object);
    }

    async findAll(filter = {}): Promise<InstanceType<T>[]> {
        return this._model.find(filter).exec();
    }

    async findOne(filter = {}): Promise<InstanceType<T>> {
        return this._model.findOne(filter).exec();
    }

    async findById(id: string): Promise<InstanceType<T>> {
        return this._model.findById(this.toObjectId(id)).exec();
    }

    async create(item: InstanceType<T>): Promise<InstanceType<T>> {
        return this._model.create(item);
    }

    async delete(id: string): Promise<InstanceType<T>> {
        return this._model.findByIdAndRemove(this.toObjectId(id)).exec();
    }

    async update(id: string, item: InstanceType<T>): Promise<InstanceType<T>> {
        return this._model.findByIdAndUpdate(this.toObjectId(id), item, { new: true }).exec();
    }

    async clearCollection(filter = {}): Promise<void>{
        return this._model.deleteMany(filter).exec();
    }

    private toObjectId(id: string): Types.ObjectId {
        return Types.ObjectId(id);
    }
}

import { Injectable } from '@nestjs/common';
import 'automapper-ts/dist/automapper';

@Injectable()
export class MapperService {

    mapper: AutoMapperJs.AutoMapper;

    constructor(){
        this.mapper = automapper;
        this.initializeMapper();
    }

    private initializeMapper(): void {
        this.mapper.initialize(MapperService.configure);
    }

    private static configure(Config: AutoMapperJs.IConfiguration): void {
        Config.createMap('User', 'UserVm')
        .forSourceMember('_id', opts => opts.ignore())
        .forSourceMember('password', opts => opts.ignore());

        Config.createMap('Users', 'UsersVm').forSourceMember('_id', opts => opts.ignore());
        Config.createMap('Users[]', 'UsersVm[]').forSourceMember('_id', opts => opts.ignore());
        Config.createMap('Note', 'NoteVm').forSourceMember('_id', opts => opts.ignore());
        Config.createMap('Note[]', 'NoteVm[]').forSourceMember('_id', opts => opts.ignore());
    } 
}

import { Injectable, Controller, HttpException, HttpStatus } from '@nestjs/common';
import { BaseService } from '../shared/base.service';
import { InjectModel } from '@nestjs/mongoose';
import { Users } from './models/users.model';
import { ModelType } from 'typegoose';
import { MapperService } from '../shared/mapper/mapper.service';
import { UsersParams } from './models/view-models/users-param.model';


@Injectable()
export class UsersService extends BaseService<Users> {
    constructor(@InjectModel(Users.modelName) private readonly _usersModel: ModelType<Users>, private readonly _mapperService: MapperService,) {
        super();
        this._model = _usersModel;
        this._mapper = _mapperService.mapper;
    }

    async createUser(params: UsersParams): Promise<Users> {
        const {username, name, notes} = params;


        const newUser = new this._model();

        newUser.username = username;
        newUser.name = name;       
        newUser.notes = notes;

        try {
            const result = await this.create(newUser);
            return result.toJSON() as Users;
        }catch(e){
            throw new HttpException(e, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }
}

